import React, {Component} from 'react';
import axios from 'axios';
import './Register.css'
import { Button } from '@material-ui/core';
import {Form} from 'react-bootstrap'
export default class Register extends Component {

    constructor(props) {
        super(props);

        this.state = {
            name: '',
            email: '',
            password : '',
            type: '',
            edcn : 1 ,
            education :[],
            ed00 : '',ed01 : '' , ed02 : '' ,
            ed10 : '',ed11 : '' , ed12 : '' ,
