import React, {Component} from 'react';
import axios from 'axios';
import './Profile.css'
class Profile extends Component {
    
    constructor(props) {
        super(props);
        this.state = {details: []};
    }

    componentDidMount() {
        axios.get('http://localhost:4000/user/profile') // unimplemented
             .then(response => {
                 this.setState({details: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
        
    }
    componentWillMount() {
        console.log(this.state.details.type)
        if(this.state.details.type === "applicant")
        {
            var x =  document.getElementsByClassName("applicant");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
        }
        else
        {
            var x =  document.getElementsByClassName("recuiter");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
        }
    }

    render() {
        return (
            <div>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>Product name</th>
                        <th>Quantity</th>
                        <th>Price</th>
                        <th>User</th>
                        {/* <th>Remain</th>
                        <th>Dispatch</th> */}

                        {/* <th>Dispatch</th> */}
                    </tr>
                </thead>
                <tbody>
                { 
                    this.state.details.map((currentproduct, i) => {
                        if(currentproduct.type === "applicant"){
                            console.log("applicant")
                        return (
                            <tr>
                                <td>{currentproduct.name}</td>
                                <td>{currentproduct.email}</td>
                                <td>{currentproduct.rating}</td>
                                <td>{currentproduct.password}</td>
                                {/* <td>{currentproduct.quantity_remain}</td>
                                <td>{currentproduct.is_dispatch}</td> */}

                                {/* <td><input type="button" value="Dispatch" className="btn btn-primary"/></td> */}
                            </tr>
                        )
                            }
                        else
                        {
                            return (
                                <span>Lmao</span>
                            )
                        }
                    })
                }
                </tbody>
            </table>
        </div>
        )
    }
}

export default Profile;
