import React, {Component} from 'react';
import axios from 'axios';

class Profile extends Component {
    
    constructor(props) {
        super(props);
        this.state = {details: []};
    }

    componentDidMount() {
        axios.get('http://localhost:4000/user/profile') // unimplemented
             .then(response => {
                 this.setState({details: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    render() {
        return (
            <div>
                 <div>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Product name</th>
                            <th>Quantity</th>
                            <th>Price</th>
                            <th>User</th>
                            <th>Remain</th>
                            <th>Dispatch</th>
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.product.map((currentproduct, i) => {
                            return (
                                <tr>
                                    <td>{currentproduct.name}</td>
                                    <td>{currentproduct.quantity}</td>
                                    <td>{currentproduct.price}</td>
                                    <td>{currentproduct.username}</td>
                                    <td>{currentproduct.quantity_remain}</td>
                                    <td><button className="btn btn-primary" onClick = {() =>{
                                        axios.get('http://localhost:4000/dispatch-change' , {
                                            params: {
                                                id:currentproduct._id
                                            }
                                        })
                                        .then(response => console.log(response))
                                        .catch(function(err) {
                                            console.log(err);
                                        });
                                        // console.log(id)
                                    }}/></td>
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
            </div>
        )
    }
}

export default Profile;
