import React, {Component} from 'react';
import axios from 'axios';
import './Profile.css'
class Profile extends Component {
    
    constructor(props) {
        super(props);
        this.state = {details: []};
    }

    componentDidMount() {
        axios.get('http://localhost:4000/user/profile') // unimplemented
             .then(response => {
                 this.setState({details: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
        if(this.state.details.type === "applicants")
        {
            var x =  document.getElementsByClassName("applicant");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
        }
        else
        {
            var x =  document.getElementsByClassName("recuiter");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
        }
    }

    render() {



                            return (
                                    <div className="yes">
                                    <div className ="common">
                                    <span>Name :</span> {this.state.details.name} <br></br>
                                    <span>Email :</span> {this.state.detailsemail}<br></br>
                                    </div>
                                    <div className = "recuiter">
                                    <span>Contact No :</span> {this.state.details.contactno} <br></br>
                                    <span>Bio :</span> {this.state.details.bio} <br></br>
                                    </div>
                                    <div className = "applicant">
                                    <span>Rating :</span> {this.state.details.rating} <br></br>
                                    </div>
                                    </div>
                            )
         
                    
    }
}

export default Profile;
