import React, {Component} from 'react';
import axios from 'axios';
import './Profile.css'
class Profile extends Component {
    
    constructor(props) {
        super(props);
        this.state = {details: []};
    }

    componentDidMount() {
        axios.get('http://localhost:4000/user/profile') // unimplemented
             .then(response => {
                 this.setState({details: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
        
    }
    componentWillMount() {
        console.log(this.state.details.type)
        if(this.state.details.type === "applicant")
        {
            var x =  document.getElementsByClassName("applicant");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
        }
        else
        {
            var x =  document.getElementsByClassName("recuiter");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
        }
    }

    render() {
        return (
            <div>
                { 
                    this.state.details.map((currentproduct, i) => {
                        if(currentproduct.type === "applicant"){
                            console.log("applicant")
                        return (
                            <div>
                            <h1>{currentproduct.name}</h1>   <br></br> 
                             <h1>  {currentproduct.email}</h1> <br></br>
                               <h1>{currentproduct.rating}</h1> <br></br>
                               <h1>{currentproduct.password}</h1> <br></br>
                            </div>
                        )
                            }
                        else
                        {
                            return (
                                <div>
                                <h1>{currentproduct.name}</h1>   <br></br> 
                                 <h1>  {currentproduct.email}</h1> <br></br>
                                   <h1>{currentproduct.contactno}</h1> <br></br>
                                   <h1>{currentproduct.bio}</h1> <br></br>
                                </div>
                            )
                        }
                    })
                }
        </div>
        )
    }
}

export default Profile;
