import React, {Component} from 'react';
import axios from 'axios';
import Navbarr from '../templates/Navbar-r'
import { Button } from '@material-ui/core';
import { Icon } from 'semantic-ui-react'


class Myjobs extends Component {

    constructor(props) {
        super(props);
        this.state = {details: [] , jobs : [] ,stat : '' , search : ''}
        this.checstatus = this.checstatus.bind(this);
        this.onSort = this.onSort.bind(this);
    }

    componentDidMount() {
        axios.get('http://localhost:4000/user/profile') // unimplemented
             .then(response => {
                 this.setState({details: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })

        axios.get('http://localhost:4000/user/active-jobs') // unimplemented
            .then(response => {
                this.setState({jobs: response.data});
            })
            .catch(function(error) {
                console.log(error);
            })

    }
    onSort(event , sortKey ,val){
        const jobs = this.state.jobs;
        jobs.sort((a,b) => {
            var nameA , nameB;
            if(sortKey === 'rating')
            {
                nameA = a.rating;
                nameB = b.raing;
            }
            else if(sortKey === 'salary')
            {
                nameA = a.salary;
                nameB = b.salary;
            }
            else
            {
                nameA = a.duration;
                nameB = b.duration;
            }
            if (nameA < nameB) {
                if(val)return -1;
                else  return 1;
            }
            if (nameA > nameB) {
                if(val) return 1;
                else return -1;
            }
            return 0;
        })
        this.setState({jobs : jobs})
        // console.log(jobs)
    }
    checstatus(_id)
    {
        var x = this.state.details[0].jobs_applied
        var i
        var f=0
        for(i = 0 ;i<x.length ;i++){
            if(x[i]==_id)
                f=1;
        }
        return f;
    }
    onChange = e => {
        var nam = e.target.name;
        var val = e.target.value;
        this.setState({ [nam] : val });
    }

    render() {
        var jobs = this.state.jobs;
        // console.log("here");
        // console.log(jobs)
        const searchedtitle = jobs.filter(job => {
            return job.title.toLowerCase().includes(this.state.search.toLowerCase())
        })
        return (
            <div>
                <Button variant="contained" color="primary" href="/dashboard-a" class="back">Back</Button>

                <input type="text" placeholder="Search by Job-Title" className="form-control" name = "search" value={this.state.search} onChange={this.onChange} />
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Job-Title</th>
                            <th>Recuiter Name</th>
                            <th>Rating <button><Icon name='angle up' size='large' onClick = {e => this.onSort(e,'rating',0)} /></button>  <Icon name='angle down' size='large' onClick = {e => this.onSort(e,'rating',1)} /></th>
                            <th>Salary <Icon name='angle up' size='large' onClick = {e => this.onSort(e,'salary',0)} />  <Icon name='angle down' size='large' onClick = {e => this.onSort(e,'salary',1)}/></th>
                            <th>Duration <Icon name='angle up' size='large' onClick = {e => this.onSort(e,'duration',0)}/>  <Icon name='angle down' size='large'onClick = {e => this.onSort(e,'duration',1)} /></th>
                            <th>Deadline</th>
                            <th>Apply</th>

                            {/* <th>Dispatch</th> */}
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        searchedtitle.map((job, i) => {
                            var f = this.checstatus(job._id)
                           // var stat
                            if(f==1)
                                this.state.stat = "Applied"
                            else
                            {
                           //     console.log(job.max_applications)
                             //   console.log(job.curr_applicants.length)
                                if(job.max_applications == job.curr_applicants.length)
                                    this.state.stat = "Full"
                                else
                                    this.state.stat = "Apply"
                            }
                            return (
                                <tr>
                                    <td>{job.title}</td>
                                    <td>{job.name_of_recuiter}</td>
                                    <td>{job.rating}</td>
                                    <td>{job.salary}</td>
                                    <td>{job.duration}</td>
                                    <td>{job.deadline}</td>
                                    <th><button className="btn btn-primary" onClick = {() =>{
                                        if(this.state.details[0].jobs_applied.length == 10)
                                        {
                                            alert("You cannot have more than 10 open job applications")
                                        }
                                        else{
                                        var sop = prompt('Please enter your name')
                                        axios.get('http://localhost:4000/user/apply-job' , {
                                            params : {
                                                _id : job._id,
                                                title : job.title,
                                                sop : sop
                                            }
                                        }
                                        )
                                        .then(response => console.log(response))
                                        .catch(function(err) {
                                            console.log(err);
                                        });
                                        axios.get('http://localhost:4000/user/add-applicant' , {
                                            params : {
                                                _id : job._id,
                                                email : this.state.details[0].email,
                                                title : job.title
                                            }
                                        }
                                        )
                                        .then(response => console.log(response))
                                        .catch(function(err) {
                                            console.log(err);
                                        });
                                    }
                                        // console.log(id)
                                    }}>{this.state.stat}</button></th>

                                    {/* <td><input type="button" value="Dispatch" className="btn btn-primary"/></td> */}
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}

export default Myjobs;
