import React, {Component} from 'react';
import axios from 'axios';
import './Register.css';

export default class Login extends Component {

    constructor(props) {
        super(props);
        this.op = React.createRef();  
        this.state = {
            email: '',
            password : ''
        }

        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangepass = this.onChangepass.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    onChangeEmail(event) {
        this.setState({ email: event.target.value });
    }

    onChangepass(event) {
        this.setState({ password: event.target.value });
    }


    onSubmit(e) {
        e.preventDefault();

        const newUser = {
            email: this.state.email,
            password : this.state.password
        }
        axios.post('http://localhost:4000/user/register', newUser)
             .then(res => {alert("Created\t" + res.data.email);console.log(res.data)})
             ;

        this.setState({
            email: '',
            password : ''
        });
    }
    showfirst(event) {
        // const tag = this.getElementById("Job-Type")
        const name = this.op.value;
    //     var applicants = React.findDOMNode(this.refs.op1);
    //     var recuiters  = React.findDOMNode(this.refs.op2);
    //     //if(name === "1" )
    //     {
    //         applicants.style.display="block";
    //         recuiters.style.display="none";
    //     }
    //  //   else
    //     {
    //         applicants.style.display="none";
    //         recuiters.style.display="block";
    //     }

    }
    render() {
        return (

            <div>
                <div className = "form-group"> 
                    <label for="Job-Type">Job-Type:</label>
                        <select id="Job-Type" ref ={this.op} onChange = {this.showfirst} >
                        <option selected="selected">Select a Navy Op Area</option>
                        <option value="1">Job Applicant</option>
                        <option value="2">Recruiter</option>
                        </select>
                </div>
                <div id = "for-applicants"ref="op1" >
                <form onSubmit={this.onSubmit}  >
                    <div className="form-group">
                        <label>Email1: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChangeEmail}
                               />  
                    </div>
                    <div className="form-group">
                        <label>Password:</label>
                        <input type = "text" className ="form-control" value = {this.state.password}
                        onChange = {this.onChangepass}
                        />
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Login" className="btn btn-primary"/>
                    </div>
                </form>
                </div>

                {/* For Recruiters */}
                <div id = "for-recuiters" ref="op2" >
                <form onSubmit={this.onSubmit} >
                    <div className="form-group">
                        <label>Email: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChangeEmail}
                               />  
                    </div>
                    <div className="form-group">
                        <label>Password:</label>
                        <input type = "text" className ="form-control" value = {this.state.password}
                        onChange = {this.onChangepass}
                        />
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Login" className="btn btn-primary"/>
                    </div>
                </form>
                </div>
            </div>
        )
    }
}
