import React, {Component} from 'react';
import axios from 'axios';
import Navbarr from '../templates/Navbar-r'
import { Button } from '@material-ui/core';

class Dashboarda extends Component {

    constructor(props) {
        super(props);
        this.state = {details: [] , jobs : [] };
        this.fun = this.fun.bind(this)
        this.onc = this.onc.bind(this)
    }
    fun(title) {
        var x = this.state.details[0].jobs_applied;
        var f=0;
        var i ;
        for( i = 0 ; i<x.length ; i++)
        {
            if(x[i]=== title)
            {
                f=1;
            }
        }
        return f;

    }
    onc(title) {
        console.log("called");
        var x = this.state.details[0].jobs_applied.slice();
        x.push(title)
        this.state.details[     0].jobs_applied = x;
        const up = {
            jobs_applied : this.state.details[0].jobs_applied
        }
        axios.post('http://localhost:4000/user/updateapplied', up)
             .then(function(res){
           //     window.location = "/login_page"
            });
    }
    componentDidMount() {
        console.log("this")
        axios.get('http://localhost:4000/user/profile') // unimplemented
             .then(response => {
                 this.setState({details: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
             console.log("this1")
        axios.get('http://localhost:4000/jobs/') // unimplemented
            .then(response => {
                this.setState({jobs: response.data});
            })
            .catch(function(error) {
                console.log(error);
            })



    }

    render() {
        return (
            <div>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Job-Title</th>
                            <th>Recuiter Name</th>
                            <th>Recuiter Rating</th>
                            <th>Salary</th>
                            <th>Duration</th>
                            <th>Deadline</th>
                            <th>Apply</th>

                            {/* <th>Dispatch</th> */}
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.jobs.map((job, i) => {

                            var f = this.fun(job.title);
                            var stat;
                            if(f===0)
                            {
                                stat = "Apply"
                            }
                            else
                            {
                                stat  = "Applied"
                            }
                            return (
                                <tr>
                                    <td>{job.title}</td>
                                    <td>{job.name_of_recuiter}</td>
                                    <td>{job.rating}</td>
                                    <td>{job.salary}</td>
                                    <td>{job.duration}</td>
                                    <td>{job.deadline}</td>
                                    <th><input type = "button" value = {stat} onClick={this.onc(job.title)}/></th>

                                    {/* <td><input type="button" value="Dispatch" className="btn btn-primary"/></td> */}
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}

export default Dashboarda;
