import React, {Component} from 'react';
import axios from 'axios';
import Navbarr from '../templates/Navbar-r'
import { Button } from '@material-ui/core';

class Dashboarda extends Component {

    constructor(props) {
        super(props);
        this.state = {details: [] , jobs : [] ,
        x : [] };
    }

    componentDidMount() {
        axios.get('http://localhost:4000/user/profile') // unimplemented
             .then(response => {
                 this.setState({details: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })

        axios.get('http://localhost:4000/jobs/') // unimplemented
            .then(response => {
                this.setState({jobs: response.data});
            })
            .catch(function(error) {
                console.log(error);
            })
        this.state.x = this.state.details[0].jobs_applied;

    }

    render() {
        return (
            <div>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Job-Title</th>
                            <th>Recuiter Name</th>
                            <th>Recuiter Rating</th>
                            <th>Salary</th>
                            <th>Duration</th>
                            <th>Deadline</th>
                            <th>Apply</th>

                            {/* <th>Dispatch</th> */}
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.jobs.map((job, i) => {

                            var f=0;
                            var y = job.title;
                            for( i = 0 ;i < x.length ;i++)
                            {
                                if(x[i]===y)
                                {
                                    f=1;
                                }
                            }
                            var stat;
                            if(f===1)
                            {
                                stat="Applied"
                            }
                            else
                            {
                                stat="Apply"
                            }
                            return (
                                <tr>
                                    <td>{job.title}</td>
                                    <td>{job.name_of_recuiter}</td>
                                    <td>{job.rating}</td>
                                    <td>{job.salary}</td>
                                    <td>{job.duration}</td>
                                    <td>{job.deadline}</td>
                                    <th><input type = "button" value = {stat}/></th>

                                    {/* <td><input type="button" value="Dispatch" className="btn btn-primary"/></td> */}
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}

export default Dashboarda;
