                               />
                    </div>
                    <div className="form-group common">
                        <label>Email: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChangeEmail}
                               />  
                    </div>
                    <div className="form-group common">
                        <label>Password: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.password}
                               onChange={this.onChange}
                               />  
                    </div>
                    <div className="form-group applicant">
                        <label>Education: </label>
                        <Button id = "addmore" onClick = {this.fun}>Add More</Button>
                        <div id = "ed-1">
                        <label>Institution Name</label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.ed00}
                               onChange={this.onChange}
                               />
                        <label>Start Year</label>
                        <input type ="number"
                                className="form-control"
                                value = {this.state.ed01}
                                onChange = {this.onChange}
                        />
                        <label>End Year</label>
                        <input type ="number"
                                className="form-control"
                                value = {this.state.ed02}
                                onChange = {this.onChange}
                        />
                        </div>
                        <div id = "ed-2">
                        <label>Institution Name</label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.ed10}
                               onChange={this.onChange}
                               />
                        <label>Start Year</label>
                        <input type ="number"
                                className="form-control"
                                value = {this.state.ed11}
                                onChange = {this.onChange}
                        />
                        <label>End Year</label>
                        <input type ="number"
                                className="form-control"
                                value = {this.state.ed12}
                                onChange = {this.onChange}
                        />
                        </div>
                        <div id = "ed-3">
                        <input type="text" 
                               className="form-control" 
                               value={this.state.ed20}
                               onChange={this.onChange}
                               />
                        <label>Start Year</label>
                        <input type ="number"
                                className="form-control"
                                value = {this.state.ed21}
                                onChange = {this.onChange}
                        />
                        <label>End Year</label>
                        <input type ="number"
                                className="form-control"
                                value = {this.state.ed22}
                                onChange = {this.onChange}
                        />
                        </div>
                    </div>

                    <div className="form-group applicant" >

                    <label>Skills:</label>

                        <select name="skills" id="skills" multiple 
                        onChange={this.onChange} value={this.state.skills}>
                        <option value="C">C</option>
                        <option value="c++">C++</option>
                        <option value="python">Python</option>
                        <option value="java">Java</option>
                        </select>
                        <label>Add More :</label>
                        <input type="text" onChange = {this.onChange} value = {this.state.skilla1}/>
                        <input type ="text" onChange = {this.onChange} value = {this.state.skilla2}/>
                        <input type ="text" onChange = {this.onChange} value = {this.state.skilla3}/>
                    </div>
                    <div className="form-group applicant">
                        <label>Rating</label>
                        <input type="range" min="0" max="5" onChange = {this.onChange} value ={this.state.rating}></input>
                    </div>
                    <div className="form-group applicant">
                        <label>CV/Resume PDF</label>
                        <input type="file" name="upload" accept="application/pdf"/>
                    </div>
                    <div className="form-group applicant">
                        <label>Profile Image in JPG format</label>
                        <input type="file" name="upload" accept="application/jpg"/>
                    </div>

                    <div className="form-group recuiter">
                        <label>Contact No: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChange}
                               />  
                    </div>
                    <div className="form-group recuiter">
                        <label>Bio: </label>
                        <textarea  maxLength="250">
                        </textarea> 
                    </div>
                    <div className="form-group common">
                        <input type="submit" value="Register" className="btn btn-primary"/>
                    </div>
                </form>
            </div>
        )
    }
