import React, {Component} from 'react';
import axios from 'axios';
import './Register.css'

export default class Register extends Component {

    constructor(props) {
        super(props);

        this.state = {
            name: '',
            email: '',
            type: ''
        }

        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeType = this.onChangeType.bind(this);
    }

    onChangeUsername(event) {
        this.setState({ name: event.target.value });
    }

    onChangeEmail(event) {
        this.setState({ email: event.target.value });
    }

    onChangeType(event) {
        this.setState({ type: event.target.value });
        //console.log("here")
        var curr = event.target.value
        if(curr=== "applicant")
        {
            var x =  document.getElementsByClassName("applicant");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
            // document.getElementsByClassName("recuiter").style.display = "none";
            // document.getElementsByClassName("common").style.display = "block";
        }
        else if(curr === "recuiter")
        {
            // document.getElementsByClassName("applicant").style.display = "none";
            // document.getElementsByClassName("recuiter").style.display = "block";
            // document.getElementsByClassName("common").style.display = "block";
        }
    }
    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    }

    onSubmit(e) {
        e.preventDefault();

        const newUser = {
            name: this.state.name,
            email: this.state.email,
            type: this.state.type 
            //date: Date.now()
        }
        axios.post('http://localhost:4000/user/register', newUser)
             .then(function(res){
                  console.log("final")
            });

        this.setState({
            name: '',
            email: '',
           // type_of_user : ''
         //   date:null
        });
    }

    render() {
        return (
            <div>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>User Type: </label>
                        <select id="opt" 
                               className="form-control" 
                               value={this.state.type}
                               onChange={this.onChangeType}
                               >
                                       <option value="select">Select</option>
                                       <option value="applicant">Applicant</option>
                                       <option value="recuiter">Recuiter</option>

                        </select>
                    </div>
                    <div className="form-group common">
                        <label>Name: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.name}
                               onChange={this.onChangeUsername}
                               />
                    </div>
                    <div className="form-group common">
                        <label>Email: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChangeEmail}
                               />  
                    </div>
                    <div className="form-group common">
                        <label>Password: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChange}
                               />  
                    </div>
                    <div className="form-group applicant" >
                        <label>Skills: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChange}
                               />  
                    </div>
                    <div className="form-group recuiter">
                        <label>Contact No: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChange}
                               />  
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Register" className="btn btn-primary"/>
                    </div>
                </form>
            </div>
        )
    }
}
