import React, {Component} from 'react';
import axios from 'axios';
import './Register.css'
import { Button } from '@material-ui/core';

export default class Register extends Component {

    constructor(props) {
        super(props);

        this.state = {
            name: '',
            email: '',
            type: '',
            edcn : 0
        }

        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeType = this.onChangeType.bind(this);
        this.fun= this.fun.bind(this);
    }
    fun() {
        edcn = edcn +1;
        var st = 'ed-' + String(edcn);
        document.getElementsById(st).style.display = "block";
        if(edcn === 3)
            document.getElementsById(addmore).style.display = "none";
    }
    onChangeUsername(event) {
        this.setState({ name: event.target.value });
    }

    onChangeEmail(event) {
        this.setState({ email: event.target.value });
    }

    onChangeType(event) {
        this.setState({ type: event.target.value });
        //console.log("here")
        var curr = event.target.value
        if(curr=== "applicant")
        {
            var x =  document.getElementsByClassName("applicant");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
             x = document.getElementsByClassName("recuiter");
             for (i = 0; i < x.length; i++) {
                x[i].style.display = "none";
              }
              x =  document.getElementsByClassName("common");
              for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
        }
        else if(curr === "recuiter")
        {
            var x =  document.getElementsByClassName("applicant");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "none";
              }
             x = document.getElementsByClassName("recuiter");
             for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
              x =  document.getElementsByClassName("common");
              for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
        }
        else
        {
            var x =  document.getElementsByClassName("applicant");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "none";
              }
             x = document.getElementsByClassName("recuiter");
             for (i = 0; i < x.length; i++) {
                x[i].style.display = "none";
              }
              x =  document.getElementsByClassName("common");
              for (i = 0; i < x.length; i++) {
                x[i].style.display = "none";
              }
        }
    }
    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    }

    onSubmit(e) {
        e.preventDefault();

        const newUser = {
            name: this.state.name,
            email: this.state.email,
            type: this.state.type 
            //date: Date.now()
        }
        axios.post('http://localhost:4000/user/register', newUser)
             .then(function(res){
                  console.log("final")
            });

        this.setState({
            name: '',
            email: '',
           // type_of_user : ''
         //   date:null
        });
    }

    render() {
        return (
            <div>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>User Type: </label>
                        <select id="opt" 
                               className="form-control" 
                               value={this.state.type}
                               onChange={this.onChangeType}
                               >
                                       <option value="select">Select</option>
                                       <option value="applicant">Applicant</option>
                                       <option value="recuiter">Recuiter</option>

                        </select>
                    </div>
                    <div className="form-group common">
                        <label>Name: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.name}
                               onChange={this.onChangeUsername}
                               />
                    </div>
                    <div className="form-group common">
                        <label>Email: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChangeEmail}
                               />  
                    </div>
                    <div className="form-group common">
                        <label>Password: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChange}
                               />  
                    </div>
                    <div className="form-group applicant">
                        <label>Education: </label>
                        <Button id = "addmore" onClick = {this.fun}>Add More</Button>
                        <div id = "ed-1">
                        <label>Institution Name</label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChange}
                               />
                        <label>Start Year</label>
                        <input type ="number"
                                className="form-control"
                                value = {this.state.email}
                                onChange = {this.onChange}
                        />
                        <label>End Year</label>
                        <input type ="number"
                                className="form-control"
                                value = {this.state.email}
                                onChange = {this.onChange}
                        />
                        </div>
                        <div id = "ed-2">
                        <label>Institution Name</label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChange}
                               />
                        <label>Start Year</label>
                        <input type ="number"
                                className="form-control"
                                value = {this.state.email}
                                onChange = {this.onChange}
                        />
                        <label>End Year</label>
                        <input type ="number"
                                className="form-control"
                                value = {this.state.email}
                                onChange = {this.onChange}
                        />
                        </div>
                        <div id = "ed-3">
                        <label>Institution Name</label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChange}
                               />
                        <label>Start Year</label>
                        <input type ="number"
                                className="form-control"
                                value = {this.state.email}
                                onChange = {this.onChange}
                        />
                        <label>End Year</label>
                        <input type ="number"
                                className="form-control"
                                value = {this.state.email}
                                onChange = {this.onChange}
                        />
                        </div>
                        <label>Institution Name</label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChange}
                               />  
                    </div>

                    <div className="form-group applicant" >
                        <label>Skills: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChange}
                               />  
                    </div>
                    <div className="form-group recuiter">
                        <label>Contact No: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChange}
                               />  
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Register" className="btn btn-primary"/>
                    </div>
                </form>
            </div>
        )
    }
}
