import React, {Component} from 'react';
import axios from 'axios';
import './Profile.css'
class Profile extends Component {
    
    constructor(props) {
        super(props);
        this.state = {details: []};
    }

    componentDidMount() {
        axios.get('http://localhost:4000/user/profile') // unimplemented
             .then(response => {
                 this.setState({details: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
        
    }
    componentWillMount() {
        console.log(this.state.details.type)
        if(this.state.details[0].type === "applicant")
        {
            var x =  document.getElementsByClassName("applicant");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
        }
        else
        {
            var x =  document.getElementsByClassName("recuiter");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
        }
    }

    render() {
        return (
            <div>

                    { 
                        this.state.details.map((user, i) => {
                            return (
                                    <div className="yes">
                                    <div className ="common">
                                    <span>Name :</span> {user.name} <br></br>
                                    <span>Email :</span> {user.email}<br></br>
                                    </div>
                                    <div className = "recuiter">
                                    <span>Contact No :</span> {user.contactno} <br></br>
                                    <span>Bio :</span> {user.bio} <br></br>
                                    </div>
                                    <div className = "applicant">
                                    <span>Rating :</span> {user.rating} <br></br>
                                    </div>
                                    </div>
                            )
                        })
                    }
            </div>
        )
    }
}

export default Profile;
