import React, {Component} from 'react';
import axios from 'axios';
import Navbarr from '../templates/Navbar-r'

class Dashboardrjobform extends Component {

    constructor(props) {
        super(props);
        this.state = {
            details: [],
            title : '',
            max_applications : 0,
            max_positions : 0,
            date : null ,
            deadline : '',
            skills : [] ,
            type : '',
            duration : 0,
            salary : 0 ,
            rating : 0,
            name_of_recuiter : '',
            email_of_recuiter : ''
        };
    }

    componentDidMount() {
        axios.get('http://localhost:4000/user/profile') // unimplemented
             .then(response => {
                 this.setState({details: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })

    }
    onChange = e => {
        var nam = e.target.name;
        var val = e.target.value;
        this.setState({ [nam] : val });
    }

    render() {
        return (
        <div>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Title: </label>
                        <input type="text" 
                               className="form-control" 
                               name = "title"
                               value={this.state.title}
                               onChange={this.onChange}
                               />
                    </div>
                    <div className="form-group">
                        <label>Max Number of Applicants: </label>
                        <input type="number" 
                             name = "max_applications"
                               className="form-control" 
                               value={this.state.max_applications}
                               onChange={this.onChange}
                               />  
                    </div>
                    <div className="form-group">
                        <label>Max Number of Positions: </label>
                        <input type="number" 
                             name = "max_positions"
                               className="form-control" 
                               value={this.state.max_positions}
                               onChange={this.onChange}
                               />  
                    </div>
                    <div className="form-group">
                        <label>Deadline : (YYYY/MM/DD/hh/mm)</label>
                        <input type="text" 
                             name = "deadline"
                               className="form-control" 
                               value={this.state.deadline}
                               onChange={this.onChange}
                               />  
                    </div>
                    <div className="form-group">
                        <label>Duration: </label>
                        <input type="number" 
                             name = "duration"
                               className="form-control" 
                               value={this.state.duration}
                               onChange={this.onChange}
                               />  
                    </div>
                    <div className="form-group">
                        <label>Salary : </label>
                        <input type="number" 
                             name = "salary"
                               className="form-control" 
                               value={this.state.salary}
                               onChange={this.onChange}
                               />  
                    </div>
                    <div className="form-group applicant">
                        <label>Rating</label>
                        <input type="range" min="0" max="5" name = "rating" onChange = {this.onChange} value ={this.state.rating}></input>
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Create" className="btn btn-primary"/>
                    </div>
                </form>
        </div>
        )
    }
}

export default Dashboardrjobform;
