            this.state.education = [ { c00 ,c01 ,c02}];
        else if(this.state.edcn === 2)
        {
            this.state.education =  [ { c00 ,c01 ,c02} , {c10 ,c11 ,c12} ]
        }
        else
        {
            this.state.edcucation  = [ { c00 ,c01 ,c02} , {c10 ,c11 ,c12} , {c20 ,c21 ,c22}  ]
        }
