import React, {Component} from 'react';
import axios from 'axios';
import './Register.css'
import { Button } from '@material-ui/core';

export default class Register extends Component {

    constructor(props) {
        super(props);

        this.state = {
            name: '',
            email: '',
            password : '',
            type: '',
            edcn : 1 ,
            education :[],
            ed00 : '',ed01 : '' , ed02 : '' ,
            ed10 : '',ed11 : '' , ed12 : '' ,
            ed20 : '',ed21 : '' , ed22 : '' ,
        }

        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeType = this.onChangeType.bind(this);
        this.fun= this.fun.bind(this);
    }
    fun() {
        this.state.edcn = this.state.edcn +1;
        var n  = this.state.edcn;
        console.log(n);
        var st = 'ed-' + n.toString();
        console.log(st);
        document.getElementById(st).style.display = "block";
        if(n === 3)
            document.getElementById("addmore").style.display = "none";
    }
    onChangeUsername(event) {
        this.setState({ name: event.target.value });
    }

    onChangeEmail(event) {
        this.setState({ email: event.target.value });
    }

    onChangeType(event) {
        this.setState({ type: event.target.value });
        //console.log("here")
        var curr = event.target.value
        if(curr=== "applicant")
        {
            var x =  document.getElementsByClassName("applicant");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
             x = document.getElementsByClassName("recuiter");
             for (i = 0; i < x.length; i++) {
                x[i].style.display = "none";
              }
              x =  document.getElementsByClassName("common");
              for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
        }
        else if(curr === "recuiter")
        {
            var x =  document.getElementsByClassName("applicant");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "none";
              }
             x = document.getElementsByClassName("recuiter");
             for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
              x =  document.getElementsByClassName("common");
              for (i = 0; i < x.length; i++) {
                x[i].style.display = "block";
              }
        }
        else
        {
            var x =  document.getElementsByClassName("applicant");
            var i;
            for (i = 0; i < x.length; i++) {
                x[i].style.display = "none";
              }
             x = document.getElementsByClassName("recuiter");
             for (i = 0; i < x.length; i++) {
                x[i].style.display = "none";
              }
              x =  document.getElementsByClassName("common");
              for (i = 0; i < x.length; i++) {
                x[i].style.display = "none";
              }
        }
    }
    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
    }

    onSubmit(e) {
        e.preventDefault();
        var c00 = this.state.ed00;
        var c01 = this.state.ed01;
        var c02 = this.state.ed02;
        if(this.state.edcn === 1)
            this.state.education = [ { c00 ,c01 ,c02}];
        // else if(this.state.edcn === 2)
        // {
        //     this.state.education =  [ { this.state.ed00 ,this.state.ed01 ,this.state.ed02} , {this.state.ed10 ,this.state.ed11 ,this.state.ed12} ]
        // }
        // else
        // {
        //     this.state.education  = [ { this.state.ed00 ,this.state.ed01 ,this.state.ed02} , {this.state.ed10 ,this.state.ed11 ,this.state.ed12} , {this.state.ed20 ,this.state.ed21 ,this.state.ed22}  ]
        // }
        const newUser = {
            name: this.state.name,
            email: this.state.email,
            type: this.state.type ,
            password : this.state.password ,
            education : this.state.education
            //date: Date.now()
        }
        axios.post('http://localhost:4000/user/register', newUser)
             .then(function(res){
                  console.log("final")
            });

        this.setState({
            name: '',
            email: '',
           // type_of_user : ''
         //   date:null
        });
    }

    render() {
        return (
            <div>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>User Type: </label>
                        <select id="opt" 
                               className="form-control" 
                               value={this.state.type}
                               onChange={this.onChangeType}
                               >
                                       <option value="select">Select</option>
                                       <option value="applicant">Applicant</option>
                                       <option value="recuiter">Recuiter</option>

                        </select>
                    </div>
                    <div className="form-group common">
                        <label>Name: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.name}
                               onChange={this.onChangeUsername}
                               />
                    </div>
                    <div className="form-group common">
                        <label>Email: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.email}
                               onChange={this.onChangeEmail}
                               />  
                    </div>
                    <div className="form-group common">
                        <label>Password: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.password}
                               onChange={this.onChange}
                               />  
                    </div>
                    <div className="form-group applicant">
                        <label>Education: </label>
                        <Button id = "addmore" onClick = {this.fun}>Add More</Button>
                        <div id = "ed-1">
                        <label>Institution Name</label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.ed00}
                               onChange={this.onChange}
                               />
                        <label>Start Year</label>
                        <input type ="number"
                                className="form-control"
                                value = {this.state.ed01}
                                onChange = {this.onChange}
                        />
                        <label>End Year</label>
                        <input type ="number"
                                className="form-control"
                                value = {this.state.ed02}
                                onChange = {this.onChange}
                        />
                        </div>
                        <div id = "ed-2">
                        <label>Institution Name</label>
                        <input type="text" 
